import UIKit

protocol PreviewPresentationLogic {
    func presentImage(image: UIImage)
}

class PreviewPresenter: PreviewPresentationLogic {
    
    weak var viewController: PreviewDisplayLogic?
    
    // MARK: Protocol
    func presentImage(image: UIImage) {
        viewController?.displayImage(image: image)
    }
    
}
